INSERT INTO `tblexpression` VALUES (1,'TK1','Trí nhớ bị suy giảm, rối loạn nhận thức, Suy giảm khả năng trí tuệ, sảng: giảm sự chú ý và ý thức'),(2,'TK2','Phối hợp các hoạt động chậm chạp, đau vai kéo dài, mệt mỏi, Run nhẹ, Gặp vấn đề khi di chuyển, Liệt cơ mặt, Mất cân bằng'),(3,'TK3','Chóng mặt, Mất thăng bằng, Ù tai, Rung giật nhãn cầu'),(4,'TK4','Không có khả năng di chuyển , Chóng mặt, Mất cảm giác về cơ thể, Đau đầu dữ dội'),(5,'TK5','Đau ở một nửa đầu, Nhạy cảm với ánh sáng, Nhạy cảm với tiếng ồn, Đột ngột thay đổi tâm trạng, dễ trở nên trầm cảm hoặc hưng phấn, Thay đổi vị giác, có khi thèm ăn hoặc có cảm giác buồn nôn, Uể oải, mệt mỏi, thường xuyên ngáp'),(6,'TM1','Động mạch vành: Đau ngực kéo dài vài phút, Có cơn khó thở về đêm\nĐộng mạch cảnh: Suy giảm trí nhớ nhanh chóng, Nhìn mờ đột ngột, Nói khó, Yếu liệt một nửa người\nMạch chi dưới: Đau chân khi đi lại, Chi lạnh, teo cơ, da khô\nĐộng mạch chủ: Đau ngực, đau lưng, đau bụng, Khối bụng đập theo nhịp tim, Chèn ép trong lồng ngực'),(7,'TM2','Nhức đầu, hoa mắt, chóng mặt, ù tai, mất ngủ nhẹ,... Đau đầu, đau ngực, khó thở , Đau nhói vùng tim, suy giảm thị lực, thở gấp, mặt đỏ bừng, da tái xanh, nôn ói, hồi hộp, đánh trống ngực, hốt hoảng'),(8,'TM3','Đau giữa ngực hoặc phía ngực bên trái, căng tức ngực, nặng giống như có ai đè lên, nóng rát ngực, co thắt ngực trái, thở khó, mệt, hụt hơi khi thở, đổ mồ hôi, choáng váng'),(9,'TM4','Khó thở, triệu chứng này tăng rõ khi nằm xuống, Mệt mỏi, Đau thắt ngực, Tim đập nhanh, đánh trống ngực, Chóng mặt, hoa mắt, Sưng chân, mắt cá chân, Ho khan, nhất là vào ban đêm'),(10,'HH1','Hắt hơi, sổ mũi; Đau rát họng gây ho, tức ngực, có khi gây khó thở.'),(11,'HH2','Nhịp thở thay đổi (khó thở); Xanh tím xung quanh miệng, móng tay, màu da nhợt nhạt; Rối loạn tim mạch(cơn nhịp nhanh, rung thất thường) , huyết áp lúc tăng lúc giảm; Rối loạn thần kinh (lẫn lộn, mất phản xạ gân xương, rối loạn ý thức)'),(12,'HH3','Ho khạc đờm từng đợt hoặc thường xuyên 3 tháng trong 1 năm và sau 2 năm liên tục.'),(13,'HH4','Ho, ho ra đờm và hụt hơi.'),(14,'HH5','Khó thở; Ho khan dai dẳng có đờm; Nhiễm trùng ngực thường xuyên; Khò khè liên tục'),(15,'HH6','Thở nhanh nông, đôi khi có thở rít, cánh mũi phập phồng. \nHo, ho có đờm lỏng hoặc đặc quánh, một số trường hợp có dính một ít máu. Tức ngực, khó thở nhẹ. Có dấu hiệu mất nước (môi khô, lưỡi trắng, má hóp, da nhăn nheo).'),(16,'TH1','Đau vùng bụng trên rốn (vùng thượng vị); Đầy bụng, khó tiêu, buồn nôn; Mất ngủ, ngủ không ngon giấc; Ợ hơi, ợ chua, hoặc nóng rát thượng vị; Rối loạn tiêu hóa.'),(17,'TH2','Tiêu chảy, buồn nôn, mất nước.'),(18,'TH3','Chướng hơi; Đau rát khi đi vệ sinh, phân rắn, có thể ra máu. Sút giảm cân nặng'),(19,'TH4','Đầy bụng, ợ hơi, ăn uống kém; Sắc mặt xám xịt; Miệng đắng, Lưỡi khô; Mệt mỏi; Đại tiện thất thường; Nhức đầu, mất ngủ'),(20,'TH5','Đau bụng phải, thượng vị, đau dai dẳng, đau nhiều khi ăn hoặc về đêm.'),(21,'TN1','Luôn muốn đi tiểu, Đường tiểu nóng, Nước tiểu có sự thay đổi về màu và mùi, Đau vùng thắt lưng, Vùng bụng dưới bị đau hoặc co rút, Rối loạn tiểu tiện'),(22,'TN2','Thường có cảm giác buồn tiểu không kiềm chế được, Đi tiểu với tần suất nhiều, Tiểu đêm, Đau rát khi đi tiểu, Rò rỉ nước tiểu khi đi ngủ'),(23,'TN3','Đi tiểu nhiều hơn 7 lần/ngày, Cảm thấy khó chịu ở phía trên bàng quang, Muốn đi tiểu nhiều lần, Không đi tiểu được'),(24,'TN4','Mệt mỏi, gầy sút, chán ăn, Đau khi đi tiểu, Đi tiểu ra máu, Tiểu rắt, tiểu khó, tiểu không tự chủ'),(25,'TN5','Đau hông vùng lưng, Đau từ lưng lan sang vùng bụng trước dưới sườn và xuống háng, Cơn đau có thể kèm buồn nôn , ói và không tìm được tư thế giảm đau'),(26,'TN6','Buồn nôn, chán ăn, Mệt mỏi, ớn lạnh, Rối loạn giấc ngủ, Thay đổi khi đi tiểu, Nấc,ngứa dai dẳng, Đau ngực, khó thở, Đau hông hưng, Hơi thở có mùi, Giảm sút tinh thần, hoa mắt chóng mặt'),(27,'TN7','Tiểu nhiều lần trong ngày, Tiểu khó, phải rặn mới tiểu được, Tiểu xong không thoải mái, Khoảng cách giữa 2 lần tiểu ngắn hơn, Đột ngột không tiểu được'),(28,'XK1','Đau khớp có tính chất cơ học, đau khi vận động, Khớp ít khi có dấu hiệu sưng nóng đỏ'),(29,'XK2','Sưng khớp, đau khớp, suy nhược cơ thể, teo cơ, hình thành các nốt mẩn dị dạng'),(30,'XK3','Đau nhức tay chân, có những cơn đau đột ngột ở cổ,thắt lưng, vai gáy,đau âm ỉ vài ngày, đau nặng hơn khi vận động, Tê bì tay chân'),(31,'XK4','Đau ở vùng cổ, thắt lưng, mất cảm giác ở vùng cột sống, Cơ bắp tay chân bị yếu, cơ thể mất cân bằng, Mất kiểm soát tiểu tiện, rối loạn thần kinh thực vật'),(32,'XK5','Đau dọc đường đi của dây thần kinh, Đau lan tỏa từ cột sống đến mông và xuống phía sau chân'),(33,'XK6','Đau cột sống ấm , Đau hơn khi vận động'),(34,'XK7','Đau lưng, giảm chiều cao, gù vẹo cột sống, gãy xương chỉ với chấn thương nhỏ'),(35,'XK8','Viêm, sưng, Đau đớn đột ngột giữa đêm');
/*!40000 ALTER TABLE `tblexpression` ENABLE KEYS */;
UNLOCK TABLES;

